window.plugin.accordion.expand = function( id ){
 var x = document.getElementById(id);
 if (x.className.indexOf('w3-show') == -1) x.className += ' w3-show'
 else x.className = x.className.replace(' w3-show', '')
}

window.plugin.accordion.instance = function ( dictionary ){
 var id = dictionary.id
 var code = this.htmlCode( dictionary )
 var node = Util.html( code )
 var children = dictionary.children
 if( children != null ){
  var inner = Util.findChild( node, id+'-inner' )
  for( var i=0; i<children.length; i++ ){
   var child = children[i]
   var childNode = null
   child.client = dictionary.client
   if( child.children ){
    if( child.color == undefined || child.color == null ) child.color = dictionary.color
    childNode = this.instance( child )
   }else childNode = window.plugin.accordionitem.instance( child )
   inner.appendChild( childNode )
  }
 }
 return node
}

window.plugin.accordion.connect = function ( dictionary ){
 this.instance(dictionary)
}
