Konekti.script.stylesheet( 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css' )

Konekti.plugin.btn.htmlCode = function(dictionary){
    if(typeof dictionary.caption == 'undefined') dictionary.caption=''
    if(typeof dictionary.fa != 'undefined' ) dictionary.caption = "<i class='fa "+dictionary.fa+"'>"+dictionary.caption+"</i>"
    if(typeof dictionary.style == 'undefined' ) dictionary.style = 'w3-bar-item w3-xlarge'
    return Konekti.util.fromTemplate( this.htmlTemplate, dictionary, '·' ) 
}
