// Google recaptcha API
window.captchaCallback = function(){
 window.plugin.captcha.loaded = true
 while( window.plugin.captcha.view.length > 0 ){
  var dictionary = window.plugin.captcha.view[0]
  window.plugin.captcha.view.shift()
  window.plugin.captcha.connect( dictionary )
 }
}

window.plugin.captcha.loaded = false
Script.loadJS('https://www.google.com/recaptcha/api.js?onload=captchaCallback&render=explicit')

// Captcha manager methods
window.plugin.captcha.view = []

window.plugin.captcha.connect = function( dictionary ){
 if( window.plugin.captcha.loaded ) grewindow.plugin.captcha.render(dictionary.id, { 'sitekey' : dictionary.key, 'callback' : window.captchaVerify } ) 
 else window.plugin.captcha.view.push(dictionary)
}

