Konekti.plugin.iframe.getText = function( id ){
 var fr = Konekti.util.vc(id)
 return fr.contentWindow.document.documentElement.outerHTML
}

Konekti.plugin.iframe.setText = function( id, txt ){
 var fr = Konekti.util.vc(id+'-frame')
 const getBlobURL = (code, type) => {
    const blob = new Blob([code], {type})
    return URL.createObjectURL(blob)
 }

 fr.src = getBlobURL(txt, 'text/html')
 // fr.src = 'data:text/plain;charset=utf-8,' + txt
}

Konekti.plugin.iframe.connect = function( dictionary ){
 var id = dictionary.id
 if(dictionary.client!=null){
  var client = Konekti.server.client[dictionary.client]
  client.editor( id, function(){ return Konekti.plugin.iframe.getText(id) }, function(txt){ Konekti.plugin.iframe.setText(id, txt) } )
 }
}
