class IFrameEditor extends KonektiEditor{
    constructor(dictionary){
        super(dictionary)
        this.gui = Konekti.util.vc(this.id+'-frame')
        if(typeof dictionary.client!='undefined'){
            Konekti.client[dictionary.client].editor(this)
        }
    }

    getText(){ return this.gui.contentWindow.document.documentElement.outerHTML }

    setText(txt){
        const getBlobURL = (code, type) => {
            const blob = new Blob([code], {type})
            return URL.createObjectURL(blob)
        }

        this.gui.src = getBlobURL(txt, 'text/html')
    }
}

Konekti.plugin.iframe.connect = function( dictionary ){
    new IFrameEditor(dictionary)
}
