class JSClient{
	constructor( id, client ){
	    this.client = client
		this.id = id
		this.edit={}
		this.annot = []
	}

	editor(e){
		this.edit[e.id]=e
		if(e.id==this.id+'JSeditor' && typeof this.client != 'undefined'){
    	    Konekti.server.client[this.client].editor(e)
    	    Konekti.server.client[this.client].edit[this.id] = e
		}    
	}

	annotation(id, annot){
	        this.annot = []
            for (var i=0; i<annot.length; i++) {
                if( annot[i].type == 'error' ) this.annot.push(annot[i])
            }
	}

	position(){
	    var editor = this.id+'JSeditor'
	    var modal = this.id+'modal'
	    Konekti.plugin.modal.show(modal, this.edit[editor].cursorIndex())
	}


	run(){
		var id = this.id
		var error = id+'JSerror'
		var editor = id+'JSeditor'
		var jsconsole = id+'JSconsole'
		var cerror = Konekti.util.vc(error)
		var cconsole = Konekti.util.vc(jsconsole)
	    if( this.annot.length == 0 ){
	        cconsole.style.display = 'block' 
	        cerror.style.display = 'none' 
    		var code = this.edit[editor].getText()
    		this.edit[jsconsole].setText("<!DOCTYPE html>\n"+
    			"<html>\n<head>\n"+
    			"	<meta charset='UTF-8'>\n"+
    			"	<meta name='viewport' content='width=device-width, initial-scale=1'>\n"+
    			"	<link rel='stylesheet' href='https://www.w3schools.com/w3css/4/w3.css'>\n</head>\n"+
    			"<body>\n"+
    			"	<div id='error' class='w3-container w3-text-red'></div>\n"+
    			"	<div id='out' style='position:relative; padding-bottom:97%; height:0; width:99%; white-space: pre-wrap;' class='w3-container'>Output:</div>"+
    			"	<script>\n"+
    			"		console.log = function(txt){document.getElementById('out').innerHTML = document.getElementById('out').innerHTML+'\\n'+txt}\n"+
    			"		try {\n"+ 
    			"		"+code+"\n"+
    			"		} catch(e){\n"+
    			"			document.getElementById('error').innerHTML = e.message\n"+
    			"		}\n"+
    			"	</"+"script>\n"+
    			"</body>\n"+
    			"</html>"
    		)
    	}else{
	        cconsole.style.display = 'none' 
	        cerror.style.display = 'block' 
    		var s = ''
    		var annot = this.annot
    	    for( var i=0; i<annot.length; i++ ){
                s += '' + annot[i].type + ':' + annot[i].text + " on line " + (annot[i].row+1) + " column " + (annot[i].column+1) + '\n'
    	    }
    	    cerror.innerHTML = s
    	}
	}
}


Konekti.plugin.javascript.connect = function( dictionary ){
    var mainclient = dictionary.client

	var id = dictionary.id
	var client = id
	dictionary.client = client

    var theClient = new JSClient(client, mainclient)
	Konekti.server.client[client] = theClient

	var nav = dictionary.navbarid

    var navbar = {
			"id":nav,
			"client":client,
			"color":"w3-blue-grey",
			"search":false,
			"btn":[	{"id":"run","fa":"fa fa-play"} ]
	}

	if(typeof dictionary.position == 'boolean' && dictionary.position )
	    navbar.btn.push({"id":"position","fa":"fa fa fa-hand-o-left"})

	Konekti.plugin.navbar.replaceWith(navbar)

	var height
	var height2
	if(typeof dictionary.half == 'string' && dictionary.half=='w3-half'){
	    height = 95 
	    height2 = 95
	}
	else{
	    height = 54.75 
	    height2 = 40.25
	}

	var editor = id+'JSeditor'
	Konekti.plugin.editor.replaceWith(
		{ 
			"id":editor,
			"height":height,
			"client":client,
			"theme":"chrome", 
			"initial":dictionary.initial, 
			"mode":"javascript"
		}
	)

	var jsconsole = id+'JSconsole'
	Konekti.plugin.iframe.replaceWith(
		{ 
			"id":jsconsole,
			"height":height2,
			"client":client,
			"src":""
		}
	)

	var modal = id+'modal'
	Konekti.plugin.modal.replaceWith(
		{ 
			"id":modal,
			"client":client,
		}
	)

}
