if(typeof window.MathJax=='undefined'){
 Konekti.script.loadJS('https://polyfill.io/v3/polyfill.min.js?features=es6')
 Konekti.script.loadJS('https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js')
}

Konekti.plugin.latex.getText = function( id ){
 var output = Konekti.util.vc(id+'content')
 return output.getAttribute('initial')
}

Konekti.plugin.latex.setText = function( id, tex ){
 var output = Konekti.util.vc(id+'content')
 output.setAttribute('initial', tex)
 output.innerHTML = tex.trim()
 var tout = null
 function set(){
  if(typeof window.MathJax!='undefined'){
   if( tout !=null ) clearTimeout(tout)
   output.innerHTML = tex.trim()
   window.MathJax.texReset()
   window.MathJax.typesetClear()
   window.MathJax.typesetPromise([output]).catch(function(err){
    output.innerHTML = ''
    output.appendChild(document.createTextNode(err.message))
    console.error(err)
   }).then(function(){});
  }else{ tout = setTimeout(set,1000) }
 }
 set()
}

Konekti.plugin.latex.connect = function( dictionary ){
 if(dictionary.client!=null){
  var id = dictionary.id
  var client = Konekti.server.client[dictionary.client]
  client.editor( id, function(){ return Konekti.plugin.latex.getText(id) }, function(tex){ Konekti.plugin.latex.setText(id,tex) } )
 }
 Konekti.plugin.latex.setText(dictionary.id, dictionary.initial)
}
