Konekti.plugin.navbar.replace = 'strict'

Konekti.plugin.navbar.htmlCode = function ( dictionary ){
	var id = dictionary.id
	var client = dictionary.client 
	var size = dictionary.size || 'w3-xlarge'
	var btnsHTML = ''
	if( dictionary.side != null ){
		var sideBtn = {'margin':'left', "tooltip":dictionary.side.tooltip, 'fa':"fa fa-bars", 'style':"w3-bar-item "+size }
		sideBtn.run = 'Konekti.plugin.sidebar.open("'+dictionary.side.id+'")'
		sideBtn.id = dictionary.side + 'Btn'
		btnsHTML += Konekti.plugin.btn.htmlCode(sideBtn)
	}

	if( dictionary.search ){
		btnsHTML += Konekti.plugin.searchbtn.htmlCode( {'id':'search2', 'find':client+".find" } )
	}

	var btn = dictionary.btn
	if( btn != null ){
		for( var i=0; i<btn.length; i++ ){
		    btn[i].style = "w3-bar-item "+size
			btn[i].run = btn[i].run || 'Konekti.client["'+client+'"].'+btn[i].id+"()"
			btnsHTML += Konekti.plugin.btn.htmlCode( btn[i] ) 
		}
	}

	// Language Button
	var server = Konekti.server
	if( server.languages!=null && server.languages[client]!=null ){
		if( Konekti.plugin.navbar.langManager == null ) Konekti.plugin.navbar.langManager = {}
		var langBtn = {'id':'language', "client":id, fa:'fa fa-language', 'style':"w3-bar-item "+size, 'options': server.languages[client].supported }
		btnsHTML += Konekti.plugin.btn.htmlCode(langBtn)
	}
	dictionary.btnsHTML = btnsHTML
    return Konekti.util.fromTemplate( this.htmlTemplate, dictionary, '·' ) 
}


Konekti.plugin.navbar.connect = function ( dictionary ){
	var id = dictionary.id
	var onclick = dictionary.client 
	var code = this.htmlCode( dictionary )
	var node = Konekti.util.html( code )
	var size = dictionary.size || 'w3-xlarge'
	if( dictionary.side != null ){
		var sideBar = dictionary.side 
		sideBar.width ='50%'
		sideBar.btnBar = id
		if( size=='w3-small' ) sideBar.btnsize = 'w3-tiny'
		else if( size=='w3-medium' ) sideBar.btnsize = 'w3-small'
		else if( size=='w3-large' ) sideBar.btnsize = 'w3-medium'
		else if( size=='w3-xlarge' ) sideBar.btnsize = 'w3-large'
		else if( size=='w3-xxlarge' ) sideBar.btnsize = 'w3-xlarge'
		else if( size=='w3-xxxlarge' ) sideBar.btnsize = 'w3-xxlarge'
		else if( size=='w3-jumbo' ) sideBar.btnsize = 'w3-xxxlarge'
		else sideBar.btnsize = size

		sideBar['class'] ="w3-button w3-bar-item  "+size
		Konekti.plugin.sidebar.replaceWith(sideBar)
	}
	if( dictionary.search ){
		Konekti.plugin.searchbtn.connect( {'id':'search2', 'find':onclick+".find" } )
	}

	// Language Button
	var server = Konekti.server
	if( server.languages!=null && server.languages[onclick]!=null ){
		if( Konekti.plugin.navbar.langManager == null ) Konekti.plugin.navbar.langManager = {}
		Konekti.client[id] = { select: function(lang){ Konekti.client[onclick].setLanguage(Konekti.client[onclick].id, lang) } }
		var langBtn = {'id':'language', "client":id, fa:'fa fa-language', 'style':"w3-bar-item "+size, 'options': server.languages[onclick].supported }
		var langNode = Konekti.plugin.dropdown.replaceWith(langBtn)
	}
}
