Konekti.plugin.navbar.instance = function ( dictionary ){
	var id = dictionary.id
	var onclick = dictionary.client 
	var code = this.htmlCode( dictionary )
	var node = Konekti.util.html( code )
	if( dictionary.side != null ){
		var sideBtn = {'margin':'left', 'fa':"fa fa-bars", 'class':"w3-button w3-bar-item  w3-margin-right w3-xlarge" }
		sideBtn.run = 'Konekti.plugin.sidebar.open("'+dictionary.side.id+'")'
		sideBtn.id = dictionary.side + 'Btn'
		node.appendChild( Konekti.plugin.btn.instance( sideBtn ) )
		var sideBar = dictionary.side 
		sideBar.width ='50%'
		sideBar.btnBar = id
		sideBar['class'] ="w3-button w3-bar-item  w3-margin-right w3-xlarge"
		Konekti.plugin.sidebar.replaceWith(sideBar)
	}
	if( dictionary.search ){
		node.appendChild( Konekti.plugin.searchbtn.instance( {'id':'search2', 'find':onclick+".find" } ) )
	}
	var btn = dictionary.btn
	if( btn != null ){
		for( var i=0; i<btn.length; i++ ){
			btn[i].run = btn[i].run || 'Konekti.server.client["'+onclick+'"].'+btn[i].id+"()"
			node.appendChild( Konekti.plugin.btn.instance( btn[i] ) )
		}
	}

	// Language Button
	var server = Konekti.server
	if( server.languages!=null && server.languages[onclick]!=null ){
		if( Konekti.plugin.navbar.langManager == null ) Konekti.plugin.navbar.langManager = {}
		Konekti.server.client[id] = { select: function(lang){ Konekti.server.client[onclick].setLanguage(Konekti.server.client[onclick].id, lang) } }
		var langBtn = {'id':'language', "client":id, fa:'fa fa-language', 'margin':'left', 'class':"w3-button w3-bar-item w3-xlarge", 'options': server.languages[onclick].supported }
		var langNode = Konekti.plugin.dropdown.instance(langBtn)
		node.appendChild( langNode )
	}
	return node
}
