Konekti.plugin.accordion.replace = 'strict'

Konekti.plugin.accordion.expand = function( id, client ){
    var c = Konekti.client[client];
    if( typeof c != 'undefined' && typeof c.expand != 'undefined' ) c.expand(id.substring(0,id.length-6))
    var x = Konekti.util.vc(id);
    var fa = id.substring(0,id.length-5)+'expand'
    var b = Konekti.util.vc(fa)
    if(x.className.indexOf('w3-show') == -1){ 
        x.className = x.className.replace('w3-hide', 'w3-show')
        b.className = 'fa fa-minus-square'
    }else{
        x.className = x.className.replace('w3-show', 'w3-hide')
        b.className = 'fa fa-plus-square'
    }    
}

Konekti.plugin.accordion.instance = function ( dictionary ){
    var id = dictionary.id
    if( typeof dictionary.symbol == 'undefined') dictionary.symbol = 'fa fa-plus-square'
    if( typeof dictionary.show == 'undefined') dictionary.show = 'w3-hide'
    var code = this.htmlCode( dictionary )
    var node = Konekti.util.html( code )
    var children = dictionary.children
    if( children != null ){
        var inner = Konekti.util.findChild( node, id+'-inner' )
        for( var i=0; i<children.length; i++ ){
            var child = children[i]
            var childNode = null
            child.client = dictionary.client
            if( child.children ){
                if( child.color == undefined || child.color == null ) child.color = dictionary.color
                childNode = this.instance( child )
            }else childNode = Konekti.plugin.accordionitem.instance( child )
            inner.appendChild( childNode )
        }
    }
    return node
}

Konekti.plugin.accordion.connect = function ( dictionary ){ this.instance(dictionary) }
