// Editor functions
Konekti.plugin.editor.done = function (){
	Konekti.plugin.editor.loaded = true
	while( Konekti.plugin.editor.view.length > 0 ){
		var dictionary = Konekti.plugin.editor.view[0]
		Konekti.plugin.editor.view.shift()
		Konekti.plugin.editor.connect( dictionary )
	}
}

Konekti.plugin.editor.loaded = false

Konekti.script.loadJS("https://ace.c9.io/build/src/ace.js", Konekti.plugin.editor.done)

Konekti.plugin.editor.view = []

Konekti.plugin.editor.ace_define = function ( lang ){
	var id = lang.mode;
	ace.define(
		"ace/mode/"+lang.mode+"_highlight_rules",
		["require","exports","module","ace/lib/oop","ace/mode/text_highlight_rules"], 
		function(require, exports, module) {
			"use strict";

			var oop = require("../lib/oop");
			var TextHighlightRules = require("./text_highlight_rules").TextHighlightRules;

			var EditorHighlightRules = function() {
				this.$rules = lang.tokens;
				this.normalizeRules();
			};

			EditorHighlightRules.metaData = {
				fileTypes: lang.fileTypes,
      			foldingStartMarker: '(%\\s*region \\w*)|([a-z]\\w*.*:- ?)',
				foldingStopMarker: '(%\\s*end(\\s*region)?)|(?=\\.)',
				keyEquivalent: '^~P',
				name: lang.qName,
				scopeName: 'source.'+lang.mode
			};


		oop.inherits(EditorHighlightRules, TextHighlightRules);

		exports.EditorHighlightRules = EditorHighlightRules;
		}
	);

	ace.define(
		"ace/mode/folding/cstyle",
		["require","exports","module","ace/lib/oop","ace/range","ace/mode/folding/fold_mode"], 
		function(require, exports, module) {
			"use strict";

			var oop = require("../../lib/oop");
			var Range = require("../../range").Range;
			var BaseFoldMode = require("./fold_mode").FoldMode;

			var FoldMode = exports.FoldMode = function(commentRegex) {
				if (commentRegex) {
					this.foldingStartMarker = new RegExp( this.foldingStartMarker.source.replace(/\|[^|]*?$/, "|" + commentRegex.start) );
	        			this.foldingStopMarker = new RegExp( this.foldingStopMarker.source.replace(/\|[^|]*?$/, "|" + commentRegex.end) );
				}
			};
			oop.inherits(FoldMode, BaseFoldMode);

			(function() {

				this.foldingStartMarker = /([\{\[\(])[^\}\]\)]*$|^\s*(\/\*)/;
				this.foldingStopMarker = /^[^\[\{\(]*([\}\]\)])|^[\s\*]*(\*\/)/;
				this.singleLineBlockCommentRe= /^\s*(\/\*).*\*\/\s*$/;
				this.tripleStarBlockCommentRe = /^\s*(\/\*\*\*).*\*\/\s*$/;
				this.startRegionRe = /^\s*(\/\*|\/\/)#?region\b/;
				this._getFoldWidgetBase = this.getFoldWidget;
				this.getFoldWidget = function(session, foldStyle, row){
					var line = session.getLine(row);

					if (this.singleLineBlockCommentRe.test(line)) { if (!this.startRegionRe.test(line) && !this.tripleStarBlockCommentRe.test(line)) return ""; }

					var fw = this._getFoldWidgetBase(session, foldStyle, row);

					if (!fw && this.startRegionRe.test(line)) return "start"; // lineCommentRegionStart	
					return fw;
				};

				this.getFoldWidgetRange = function(session, foldStyle, row, forceMultiline) {
					var line = session.getLine(row);

					if (this.startRegionRe.test(line)) return this.getCommentRegionBlock(session, line, row);

					var match = line.match(this.foldingStartMarker);
					if (match) {
						var i = match.index;

						if (match[1]) return this.openingBracketBlock(session, match[1], row, i);

						var range = session.getCommentFoldRange(row, i + match[0].length, 1);

						if (range && !range.isMultiLine()){ 
							if (forceMultiline) { range = this.getSectionRange(session, row); }
							else if (foldStyle != "all") range = null;
						}

						return range;
					}

					if (foldStyle === "markbegin") return;

					var match = line.match(this.foldingStopMarker);
				        if (match) {
							var i = match.index + match[0].length;

							if (match[1]) return this.closingBracketBlock(session, match[1], row, i);

							return session.getCommentFoldRange(row, i, -1);
					}
				};

				this.getSectionRange = function(session, row) {
					var line = session.getLine(row);
					var startIndent = line.search(/\S/);
					var startRow = row;
					var startColumn = line.length;
					row = row + 1;
					var endRow = row;
					var maxRow = session.getLength();
					while (++row < maxRow) {
						line = session.getLine(row);
						var indent = line.search(/\S/);
						if (indent === -1) continue;
						if  (startIndent > indent) break;
						var subRange = this.getFoldWidgetRange(session, "all", row);

						if (subRange) {
							if (subRange.start.row <= startRow) { break; }
							else if (subRange.isMultiLine()) { row = subRange.end.row; } 
								 else if (startIndent == indent) { break; }
						}
						endRow = row;
			        }

					return new Range(startRow, startColumn, endRow, session.getLine(endRow).length);
				};

				this.getCommentRegionBlock = function(session, line, row) {
					var startColumn = line.search(/\s*$/);
					var maxRow = session.getLength();
					var startRow = row;

					var re = /^\s*(?:\/\*|\/\/|--)#?(end)?region\b/;
					var depth = 1;
					while (++row < maxRow) {
						line = session.getLine(row);
						var m = re.exec(line);
						if (!m) continue;
						if (m[1]) depth--;
						else depth++;
						if (!depth) break;
					}

					var endRow = row;
					if (endRow > startRow) { return new Range(startRow, startColumn, endRow, line.length); }
				};

			}).call(FoldMode.prototype);

		}
	);

	ace.define(
		"ace/mode/"+id,
		["require","exports","module","ace/lib/oop","ace/mode/text","ace/mode/"+id+"_highlight_rules","ace/mode/folding/cstyle"],
		function(require, exports, module) {
			"use strict";

			var oop = require("../lib/oop");
			var TextMode = require("./text").Mode;
			var EditorHighlightRules = require("./"+id+"_highlight_rules").EditorHighlightRules;
			var FoldMode = require("./folding/cstyle").FoldMode;

			var Mode = function() {
				this.HighlightRules = EditorHighlightRules;
				this.foldingRules = new FoldMode();
				this.$behaviour = this.$defaultBehaviour;
			};
			oop.inherits(Mode, TextMode);

			(function() {
				this.lineCommentStart = "%";
				this.blockComment = {start: "/*", end: "*/"};
				this.$id = "ace/mode/"+id;
			}).call(Mode.prototype);

			exports.Mode = Mode;
		}
	);
}

Konekti.plugin.editor.load = function ( id ){ 
	Konekti.plugin.editor.view.push( id )
	if( Konekti.plugin.editor.loaded ) Konekti.plugin.editor.done();
}

Konekti.plugin.editor.connect = function ( dictionary ){
	if( Konekti.plugin.editor.loaded ){ new ACEEditor(dictionary) }else Konekti.plugin.editor.view.push( dictionary )
}

class ACEEditor extends KonektiEditor{
	constructor(dictionary){
	    super(dictionary) 
	    var id = this.id
	    this.gui = Konekti.util.vc(id+'-ace')
	    this.edit = ace.edit(id+'-ace');
        this.sui = this.gui.getElementsByClassName('ace_scroller')[0]
	    this.sbui = this.gui.getElementsByClassName('ace_scrollbar-v')[0].getElementsByClassName('ace_scrollbar-inner')[0]

		if( dictionary.code != null ){
			dictionary.code.cid = id
			Konekti.plugin.editor.ace_define(dictionary.code)
		} 

		this.edit.setFontSize("16px")
		if(typeof dictionary.initial != 'undefined'){
		    this.gui.setAttribute('initial', dictionary.initial)
		    this.edit.setValue(dictionary.initial,1)
		} 

		this.edit.session.setMode("ace/mode/"+dictionary.mode);
		if( dictionary.theme!=null) this.edit.setTheme("ace/theme/"+dictionary.theme);
		this.edit.setShowPrintMargin(false);
		if(dictionary.client!=null){
			var client = Konekti.client[dictionary.client]
			client.editor(this)
            var x = this
            this.edit.session.on("changeAnnotation", function () {
                var annot = x.edit.session.getAnnotations();
                client.annotation(id, annot)                
            });	


			if(typeof dictionary.onchange == 'boolean' && dictionary.onchange)
			    this.edit.session.on('change', function(){ client.onchange(id) })
		}

	}

	getText(){ return this.edit.getValue() }

	setText(txt){ this.edit.setValue(txt, 1) }

	locateCursor(row, column){
    	this.edit.moveCursorTo(row, column);
    	this.edit.focus();
	}

	cursorLocation(){ return this.edit.selection.getCursor() }

	highlight(row){ this.locateCursor( row,1 ) }


	cursorIndex(){ return this.edit.session.doc.positionToIndex(this.edit.selection.getCursor()) }

	locateCursorIndex(pos){ this.edit.selection.moveTo(this.edit.session.doc.indexToPosition(pos)) }

	scrollTop(pos){
	    var x=this
    	var tout
    	function check(){
    	    var h = parseInt(x.sbui.style.height, 10)
    	    var ls = parseInt(x.sui.style.lineHeight,10)
    	    var tlines = h/ls
    	    if(tlines != x.edit.session.getLength() ) tout = setTimeout(check,100)
            else{
            	if(typeof pos=='undefined') pos = h
            	var line = Math.floor(pos/ls)
                x.edit.scrollToLine(line, true, true, function () {});
                x.edit.gotoLine(line, 0, true);
                clearTimeout(tout)
            }
    	}
    	check()
	}
}
