Konekti.plugin.app.htmlCode = function(dictionary){
    if( typeof dictionary.font == 'undefined' ) dictionary.font = 'xlarge'
    var fs = Math.floor(1.5 * Konekti.util.fontSize(dictionary.font))
    var titleHeight = fs+2
    var navbarHeight = fs + 16
    dictionary.rheight  = titleHeight + navbarHeight
    return Konekti.util.fromTemplate( this.htmlTemplate, dictionary, '·' )
}

Konekti.plugin.app.replaceWith = function(dictionary){
	document.body.innerHTML = this.htmlCode(dictionary)
}
