Konekti.core.stylesheet( 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css' )
Konekti.core.stylesheet( 'https://www.w3schools.com/w3css/4/w3.css' )

Konekti.plugin.btn.fillLayout = function(dictionary){
    dictionary.client = dictionary.client || 'client'
    if( dictionary.run === undefined || dictionary.run === null ){
        dictionary.run = 'Konekti.client("'+dictionary.client+'").'
        if( typeof dictionary.arg === 'boolean' && dictionary.arg ) 
            dictionary.run += 'select("'+dictionary.id+'")'
	    else 
	        dictionary.run += dictionary.id+"()"
    }
    dictionary.caption = dictionary.caption || ''
    dictionary.title = dictionary.title || ''
    dictionary.caption = "<i id='"+dictionary.id+"-icon' class='"+(dictionary.icon !== undefined?dictionary.icon:"")+"'></i> "+dictionary.caption
    dictionary.style = dictionary.style || 'w3-bar-item w3-xlarge'
    return Konekti.core.fromTemplate( this.htmlTemplate, dictionary) 
}

Konekti.btn = function(toolbar, id, caption_config, onclick){
    var dict
    if(arguments.length==3) dict = caption_config
    else dict = {"caption":caption_config, "run":onclick}
    dict.id = id
    Konekti.plugin.btn.appendAsChild(toolbar,dict)
}
