// Google recaptcha API
window.captchaCallback = function(){
    Konekti.plugin.captcha.loaded = true
    while( Konekti.plugin.captcha.view.length > 0 ){
        var dictionary = Konekti.plugin.captcha.view[0]
        Konekti.plugin.captcha.view.shift()
        Konekti.plugin.captcha.connect( dictionary )
    }
}

Konekti.plugin.captcha.loaded = false
Script.loadJS('https://www.google.com/recaptcha/api.js?onload=captchaCallback&render=explicit')

// Captcha manager methods
Konekti.plugin.captcha.view = []

Konekti.plugin.captcha.connect = function( dictionary ){
    if( Konekti.plugin.captcha.loaded ) greKonekti.plugin.captcha.render(dictionary.id, { 'sitekey' : dictionary.key, 'callback' : window.captchaVerify } ) 
    else Konekti.plugin.captcha.view.push(dictionary)
}

