Konekti.plugin.dropdown.replace = 'strict'

class DropDown extends KonektiClient{
	constructor( dictionary ){
		super(dictionary)
		dictionary.select = dictionary.select || 'select'
		dictionary.client = dictionary.client || 'client'
	}

	drop(){
        var x = Konekti.vc(this.dictionary.id+'-drop');
        if (x.className.indexOf("w3-show") == -1) {
            x.className += " w3-show";
        } else { 
            x.className = x.className.replace(" w3-show", "")
        }
	}

	select( option ){
	    this.drop()
        var c = Konekti.client(this.dictionary.client)
        if( typeof c != 'undefined' && c[this.dictionary.select] !== undefined ) 
            c[this.dictionary.select](this.dictionary.id, option)
	}
}

Konekti.dropdown = function(t){
    Konekti.plugin.dropdown.replaceWith(t)
    return new DropDown(t)
}

Konekti.plugin.dropdown.splitTemplate = function(){
    var k = this.htmlTemplate.indexOf("</div>\n<div") + 7
    this.itemTemplate = this.htmlTemplate.substring(0,k)
    this.htmlTemplate = this.htmlTemplate.substring(k)
}

Konekti.plugin.dropdown.fillLayout = function(dictionary){
    if( this.itemTemplate === undefined ) this.splitTemplate()
    dictionary.caption = dictionary.caption || ''
    dictionary.style = dictionary.style || 'w3-bar-item w3-xlarge'
    dictionary.icon = dictionary.icon || ''
    var optTemplate = ''
    var option = dictionary.options
    for( var i=0; i<option.length; i++ ){
            if( typeof option[i] == 'string'){
                option[i] = {"id":option[i], "caption":option[i]}
            }
            option[i].client = dictionary.id
            optTemplate += Konekti.core.fromTemplate(this.itemTemplate, option[i])
    }
    dictionary.drop = optTemplate
    return Konekti.core.fromTemplate(this.htmlTemplate, dictionary)
}
