class DIVEditor extends KonektiEditor{
    constructor(dictionary){
        super(dictionary)
    }

    getText(){ return this.gui.innerHTML }

    setText(txt){ this.gui.innerHTML = txt }
}


Konekti.plugin.html.connect = function( dictionary ){
    new DIVEditor(dictionary)
}

Konekti.html = function(container_id, code, client){
    var dict = {"id":container_id, "initial":code} 
    if(client!==undefined) dict.client = client
    Konekti.plugin.html.replaceWith( dict )
}
