class KonektiLangBtnModule extends KonektiClient{
    constructor(dictionary){
        super( dictionary.id ) 
        this.dictionary = dictionary
        dictionary.content.client = dictionary.id
    }

    select(lang){ Konekti.client[this.dictionary.client].setLanguage(Konekti.client[this.dictionary.client].id, lang) }

    toogle(){
        var module = this
        var node = this.vc('Content')
        if (node.className.indexOf("w3-show") == -1){
    		node.className += " w3-show"
    	}else{
    		node.className = node.className.replace(" w3-show", "")
    	}
    }
}

Konekti.plugin.langbtn.connect = function ( dictionary ){
	dictionary.content = {}
    dictionary.content.id = dictionary.id + 'Content'
    new KonektiLangBtnModule(dictionary)
	var id = dictionary.id
	var client = dictionary.client 
	var font = dictionary.font || 'xlarge'
	// Language Button
	if( Konekti.languages!=null && Konekti.languages[client]!=null ){
	    dictionary.content.font = Konekti.util.previousFont(font)
	    dictionary.content.style = dictionary.style
		dictionary.content.options = Konekti.languages[client].supported 
	    Konekti.plugin.dropdown.replaceWith(dictionary.content)
	}
}
