Konekti.plugin.langbtn.replace = 'strict'

Konekti.plugin.langbtn.htmlCode = function ( dictionary ){
	var id = dictionary.id
	var client = dictionary.client 
	var size = dictionary.size || 'w3-xlarge'

	// Language Button
	if( Konekti.languages!=null && Konekti.languages[client]!=null ){
		var langBtn = {'id':'language', "client":id, fa:'fa fa-language', 'style':"w3-bar-item "+size, 'options': Konekti.languages[client].supported }
		return Konekti.plugin.btn.htmlCode(langBtn)
	}
	return ""
}


Konekti.plugin.langbtn.connect = function ( dictionary ){
	var id = dictionary.id
	var client = dictionary.client 
	var size = dictionary.size || 'w3-xlarge'
	// Language Button
	if( Konekti.languages!=null && Konekti.languages[client]!=null ){
		Konekti.client[id] = { select: function(lang){ Konekti.client[client].setLanguage(Konekti.client[client].id, lang) } }
		var langBtn = {'id':'language', "client":id, fa:'fa fa-language', 'style':"w3-bar-item "+size, 'options': Konekti.languages[client].supported }
		var langNode = Konekti.plugin.dropdown.replaceWith(langBtn)
	}
}
