if(typeof window.MathJax=='undefined'){
 Konekti.core.loadScript('text/javascript', 'https://polyfill.io/v3/polyfill.min.js?features=es6')
 Konekti.core.loadJS('https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml')
}

Konekti.latex = function(container_id, tex, client){
    var dictionary = {"id":container_id}
    if( tex !== undefined ) dictionary.initial = tex
    if( client !== undefined ) dictionary.client = client
    Konekti.plugin.latex.replaceWith(dictionary)
}

Konekti.plugin.latex.fillLayout = function( dictionary ){
	var c = Konekti.vc( dictionary.id )
	if( typeof dictionary.initial == 'string')	c.setAttribute('initial',dictionary.initial)
	else c.setAttribute('initial', '')
	return Konekti.core.fromTemplate( this.htmlTemplate, dictionary, String.fromCharCode(183) )
}

Konekti.plugin.latex.connect = function( dictionary ){ new LatexEditor(dictionary) }

class LatexEditor extends KonektiEditor{
	constructor(dictionary){
	    super(dictionary) 
	    this.innergui = this.vc('content')
        //if(typeof dictionary.client != 'undefined') Konekti.client(dictionary.client).editor(this)
        this.setText(dictionary.initial)
	}

	getText(){ return this.gui.getAttribute('initial') }

	setText(tex){
	    if( typeof tex == 'undefined' || tex==null) text=''
        this.gui.setAttribute('initial', tex)
	    var output = this.innergui
        output.innerHTML = tex.trim()
        var tout = null
        function set(){
            if(typeof window.MathJax!='undefined'){
                if( tout !=null ) clearTimeout(tout)
                output.innerHTML = tex.trim()
                window.MathJax.texReset()
                window.MathJax.typesetClear()
                window.MathJax.typesetPromise([output]).catch(function(err){
                    output.innerHTML = ''
                    output.appendChild(document.createTextNode(err.message))
                    console.error(err)
                }).then(function(){});
            }else{ tout = setTimeout(set,1000) }
        }
        set()
	}
}
