Konekti.plugin.media.replaceWith = function( dictionary ){
    var c = Konekti.util.vc( dictionary.id )
    c.innerHTML = this.htmlCode(dictionary)
console.log(c.innerHTML)    
    new Media(dictionary)
}

/**
 * A media manager.
 */
class Media extends KonektiMedia{
	constructor( dictionary ){
		super(dictionary)
		this.media = Konekti.util.vc(this.id+'content')

        if( typeof dictionary.client != 'undefined' ){
    		var media = this.media
            var id = dictionary.id
            var client = Konekti.client[dictionary.client]
            function playing(e){ client.playing(id, media.currentTime) }
            function stop(e){ client.paused(id) }
            media.addEventListener('play', playing);
            media.addEventListener('pause', stop);
            media.ontimeupdate = function(){ client.playing(id, media.currentTime) };
            client[this.id] = this
        }
	}

	pause(){ this.media.pause() }

	play(){ this.media.play() }

	seek(time){ this.media.currentTime = time }
}
