Konekti.media = function(container_id, media, type, src, client){
    var dict = {"id":container_id, "media":media, "type":type, "src":src}
    if(client !== undefined ) dict.client=client
    Konekti.plugin.media.replaceWith(dict)
}

Konekti.video = function(container_id, type, src, client){
    this.media(container_id, "video", type, src, client)
}

Konekti.mp4 = function(container_id, src, client){
    this.media(container_id, "video", "mp4", src, client)
}

Konekti.audio = function(container_id, type, src, client){
    this.media(container_id, "audio", type, src, client)
}

Konekti.mp3 = function(container_id, src, client){
    this.media(container_id, "audio", "mp3", src, client)
}

Konekti.plugin.media.connect = function( dictionary ){
   new Media(dictionary)
}

/**
 * A media manager.
 */
class Media extends KonektiMedia{
	constructor( dictionary ){
		super(dictionary)
		this.media = this.vc('Content')

        if( typeof dictionary.client != 'undefined' ){
    		var media = this.media
            var id = dictionary.id
            var client = Konekti.client(dictionary.client)
            function playing(e){ client.playing(id, media.currentTime) }
            function stop(e){ client.paused(id) }
            media.addEventListener('play', playing);
            media.addEventListener('pause', stop);
            media.ontimeupdate = function(){ client.playing(id, media.currentTime) };
            client[this.id] = this
        }
	}

	pause(){ this.media.pause() }

	play(){ this.media.play() }

	seek(time){ this.media.currentTime = time }
}
