Konekti.plugin.modal.replace = 'strict'

Konekti.plugin.modal.show = function(id, obj){
    if( typeof obj.plugin !== 'undefined'){
       obj.id = id+'content'
       Konekti.build(obj, obj.client)
    }else{
        var cui = Konekti.util.vc(id+'content')
        cui.innerHTML = obj    
    } 
    var ui = Konekti.util.vc(id)
    ui.style.display='block'
}

Konekti.plugin.modal.close = function(id){
    Konekti.util.vc(id).style.display='none'    
}    

Konekti.plugin.modal.cloudfilemanager = function(id, client, url){
	function callback( tree ){
		function back2(plugin){
		    plugin.tree = tree
		    plugin.id = id+'content'
		    plugin.client = 'modalfilemanager'
		    plugin.cloud = true
    		Konekti.plugin.modal.show(id,plugin)
		}
		Konekti.getConfigFile('filemanager', back2)
	}
	var fm = new CloudFileManager('modalfilemanager',client,url)
    Konekti.client['modalfilemanager'] = fm
	fm.getTree(callback)
}
