Konekti.plugin.navbar.replace = 'strict'

Konekti.plugin.navbar.fillLayout = function ( dictionary ){
	var id = dictionary.id
	var client = dictionary.client 
	var size = dictionary.size || 'w3-xlarge'
	var btnsHTML = ''

	var btn = dictionary.btn
	if( btn != null ){
		for( var i=0; i<btn.length; i++ ){
		    btn[i].style = (btn[i].style!==undefined?btn[i].style:'')+" w3-bar-item "+size
		    btn[i].client = btn[i].client || client
		    btn[i].arg = btn[i].arg || dictionary.arg
			btnsHTML += Konekti.plugin.btn.fillLayout( btn[i] ) 
		}
	}

	dictionary.btnsHTML = btnsHTML
    return Konekti.core.fromTemplate( this.htmlTemplate, dictionary) 
}

Konekti.navbar = function(container_id, btns, color, size, client){
    var dict = {"id":container_id, "btn":btns, "color":"w3-blue-grey", "size":"w3-xlarge"}
    if(color !== undefined && color!==null ) dict.color = color
    if(size !== undefined && size!==null) dict.size = size
    if(client!==undefined) dict.client = client
    Konekti.plugin.navbar.replaceWith(dict)
}
