Konekti.plugin.sidebar.replace = 'strict'

Konekti.plugin.sidebar.open = function( barId ){ Konekti.vc(barId).style.display = 'block' }

Konekti.plugin.sidebar.close = function( barId ){ Konekti.vc(barId).style.display = 'none' }

Konekti.sidebar = function(container_id, width, height){
    width = width || '200px'
    height = height || '87%'
    Konekti.plugin.sidebar.replaceWith({'id':container_id, 'width':width, 'height':height})
}
