Konekti.plugin.split.fillLayout = function(dictionary) {
    var id = dictionary.id
    dictionary.type = dictionary.type || 'col'
    var x = dictionary.start || 50
    if (dictionary.type == 'col') {
        dictionary.mode = 'h'
        dictionary.float='float:left'
        dictionary.hOne = dictionary.hDrag = dictionary.hTwo = '100%'
        dictionary.wOne = x + "%"
        dictionary.wDrag = '1%'
        dictionary.wTwo = (99-x)+'%'
    } else {
        dictionary.mode = 'v'
        dictionary.float = ''
        dictionary.wOne = dictionary.wDrag = dictionary.wTwo = '100%'
        dictionary.hOne = x + "%"
        dictionary.hDrag = '1%'
        dictionary.hTwo = (99-x)+'%'
    }
    return Konekti.core.fromTemplate(this.htmlTemplate, dictionary)
}

class KonektiSplit extends KonektiClient{
    constructor( dictionary ){
        super(dictionary)
        this.type = dictionary.type
    }
    
    dragstart(e) {
        e.preventDefault();
        this.dragging = true;
    }
    
    dragmove(e) {
        var id = this.dictionary.id
        if (this.dragging){
            var c = Konekti.vc(this.dictionary.id)
            var r = c.getBoundingClientRect()
            if (this.type == "col") {
                var leftPercentage = 100 * (e.pageX-r.left) / r.width
                if (leftPercentage > 5 && leftPercentage < 98) {
                    var rightPercentage = 100-leftPercentage;
                    Konekti.vc(this.id+'-one').style.width = (leftPercentage) + "%";
                    Konekti.vc(this.id+'-two').style.width = (rightPercentage-1) + "%";
                }
            } else {
                var leftPercentage = 100 * (e.pageY-r.top) / r.height
                if (leftPercentage > 5 && leftPercentage < 98) {
                    var rightPercentage = 100-leftPercentage;
                    Konekti.vc(this.id+'-one').style.height = (leftPercentage-1) + "%";
                    Konekti.vc(this.id+'-two').style.height = (rightPercentage-1) + "%";
                }
            }
        }
    }

    dragend() {
        this.dragging = false;
        var vend = navigator.vendor;
        if (window.editor && vend.indexOf("Apple") == -1) {
            window.editor.refresh();
        }
    }    
}

Konekti.plugin.split.connect = function( dictionary ){
    var id = dictionary.id
    
    var c = Konekti.vc(id+'-drag')
    c.addEventListener("mousedown", function(e){ Konekti.client(id).dragstart(e);} )
    c.addEventListener("touchstart", function(e){ Konekti.client(id).dragstart(e); } )
    window.addEventListener("mousemove", function(e){ Konekti.client(id).dragmove(e); } )
    window.addEventListener("touchmove", function(e){ Konekti.client(id).dragmove(e); } )
    window.addEventListener("mouseup", function(){ Konekti.client(id).dragend(); } )
    window.addEventListener("touchend", function(){ Konekti.client(id).dragend(); } )
}

Konekti.split = function( id, type, percentage){
    var d = {"id":id, "type":type, "start":percentage}
    var c = new KonektiSplit(d)
    Konekti.plugin.split.replaceWith(d)
    return c
}
