class TabClient{
	constructor( id, tabs ){
		this.id = id
		this.tabs = tabs
	}

    open(tabPage) {
        for (var i = 0; i < this.tabs.length; i++) {
            Konekti.util.vc(this.tabs[i]).style.display = "none"  
        }
        Konekti.util.vc(tabPage).style.display = "block"
    }

	editor(e){}

}

Konekti.plugin.tab.htmlCode = function( dictionary ){
    var tabsHTML =''
    for( var i=0; i<dictionary.tab.length; i++ ){
        tabsHTML +="<div id='"+dictionary.tab[i].id+"' style='position:relative;width:100%;height:100%' ></div>"
    }
    dictionary.tabs = tabsHTML
    return Konekti.util.fromTemplate( this.htmlTemplate, dictionary, '·' )    
}

Konekti.plugin.tab.connect = function( dictionary ){
    var mainclient = dictionary.client

	var id = dictionary.id
	var client = id
//	dictionary.client = client

	var btn = []
	var initial = dictionary.initial || dictionary.tab[0].id
    var tabs=[]
    for( var i=0; i<dictionary.tab.length; i++ ){
        tabs.push(dictionary.tab[i].id)
        var btx = dictionary.tab[i].btn
        btx.run = 'Konekti.client["'+client+'"].open("'+dictionary.tab[i].id+'")'
        btn.push(btx)
    }

    var theClient = new TabClient(client, tabs)
	Konekti.client[client] = theClient
	setTimeout( function(){ theClient.open(dictionary.initial) }, 200 )

	var nav = dictionary.navbarid || id+'tabbar'
    var navbar = {
			"id":nav,
			"client":client,
			"color":"w3-blue-grey",
			"search":false,
			"btn":btn
	}

	Konekti.plugin.navbar.replaceWith(navbar)

}
