class TabClient extends KonektiClient{
	constructor( id, tabs ){
		super(id)
		this.tabs = tabs
	}

    open(tabPage) {
        if(this.current===undefined) this.current=tabPage
        if(this.current!==tabPage) Konekti.vc(this.current+'-tab').style.borderStyle='none'
        this.current=tabPage
        Konekti.vc(this.current+'-tab').style.borderStyle='groove'
        for (var i = 0; i < this.tabs.length; i++) {
            Konekti.vc(this.tabs[i]).style.display = "none"  
        }
        Konekti.vc(tabPage).style.display = "block"
        var ta = Konekti.vc(tabPage).getElementsByTagName('textarea')
        for( var k=0; k<ta.length; k++ ) ta[k].focus()
    }

	editor(e){}

}

Konekti.plugin.tab.fillLayout = function( dictionary ){
    var tabsHTML =''
    for( var i=0; i<dictionary.tab.length; i++ ){
        tabsHTML +="<div id='"+dictionary.tab[i].id+"' style='position:relative;width:100%;height:100%' ></div>"
    }
    dictionary.tabs = tabsHTML
    return Konekti.core.fromTemplate( this.htmlTemplate, dictionary)    
}

Konekti.plugin.tab.connect = function( dictionary ){
    var mainclient = dictionary.client

	var id = dictionary.id
	var client = id
//	dictionary.client = client

	var initial = dictionary.initial || dictionary.tab[0].id
    var tabs=[]
    for( var i=0; i<dictionary.tab.length; i++ ){
        tabs.push(dictionary.tab[i].id)
        var btx = dictionary.tab[i]
        btx.id += '-tab'
        btx.run = 'Konekti.client("'+client+'").open("'+tabs[i]+'")'
    }

    var tabClient = new TabClient(client, tabs)
	setTimeout( function(){ tabClient.open(dictionary.initial) }, 200 )

	var nav = dictionary.navbarid || id+'-bar'
    var navbar = {
			"id":nav,
			"client":client,
			"color":dictionary.color || "w3-blue-grey",
			"search":false,
			"size":'w3-medium',
			"btn":dictionary.tab
	}

	Konekti.plugin.navbar.replaceWith(navbar)
}

Konekti.tab = function(container_id, config){
    config.id = container_id
    var i
    var k = 2
    var btns = []
    for(i=k; i<arguments.length; i++){
 if(typeof arguments[i]==='string'){
                    btns.push({'id':arguments[i], 'caption':arguments[i]})
        }else{
        btns.push(arguments[i])
        }
    }
    config.tab = btns
    Konekti.plugin.tab.replaceWith(config)
    return Konekti.client(config.id)
}
