Konekti.plugin.title.replace = 'strict'

Konekti.core.loadJS('https://konekti.numtseng.com/source/KonektiFont')

Konekti.plugin.title.fillLayout = function(dictionary){
    if( typeof dictionary.color === 'undefined' ) dictionary.color = 'w3-blue'
    if( typeof dictionary.font === 'undefined' ) dictionary.font = 'w3-xlarge'
    dictionary.pfont = Konekti.core.font.previous(dictionary.font)
   return Konekti.core.fromTemplate( this.htmlTemplate, dictionary, '·' )
}
Konekti.title = function(container_id, title_config, subtitle){
    var dict
    if(typeof title_config==='string'){
        dict={}
        dict.main=title_config
        if(subtitle!==undefined) dict.secondary=subtitle
        else dict.secondary=''
    }else dict=title_config
    dict.id=container_id
    Konekti.plugin.title.replaceWith(dict)
}

Konekti.header = function( container_id, text, size, color ){
    Konekti.title(container_id, {'main':text,'font':size!==undefined?size:'w3-xlarge', 'secondary':'', 'color':color!==undefined?color:'w3-blue'}) }
