class KonektiUserBtnModule extends KonektiClient{
    constructor(dictionary){
        super( dictionary.id ) 
        this.dictionary = dictionary
        dictionary.content.client = dictionary.id
    }

    logout(){
        this.toogle()
        Konekti.client[this.dictionary.client].logout()
    }

    setUser( user ){
        this.toogle()
        Konekti.client[this.dictionary.client].setUser(user)
    }

    toogle(){
        var module = this
	    var node = this.vc('Content')
	    if (node.style.display === "none"){
    		node.style.display = ""
    		node = this.vc('Btn')
    		node.innerHTML = '<i class="fa fa-user"></i>'
    		node.onclick=null
    	}else{
    		node.style.display = "none";
    		node = this.vc('Btn')
    		node.innerHTML = '<i class="fa fa-power-off"></i>'
    		node.onclick= function(){ 
    	        var endpoint = new AplikigoEndPoint(module.dictionary.content.endpoint.url)
    	        endpoint.request(module.id+'Content', 'logout', [Konekti.user.credential])
    		}     
    	}
    }
}

Konekti.plugin.userbtn.replace='strict' 
Konekti.plugin.userbtn.connect = function( dictionary ){
    new KonektiUserBtnModule(dictionary)
    dictionary.content.id = dictionary.id + 'Content'

    Konekti.plugin.user.replaceWith(dictionary.content)

    if(typeof dictionary.toogle==='boolean' && dictionary.toogle) 
        Konekti.client[dictionary.id].toogle()
/*
