Konekti.plugin.searchbtn.connect = function( dictionary ){
    var client = dictionary.client
    var theClient = Konekti.client[client]
    var id = dictionary.id
    var input = Konekti.util.vc(id+'Input')
    function check(event){ theClient.find_check( event.target.value ) }

    function process(event){
        // Process the 'Enter' key on the keyboard
        if(event.keyCode === 13){
            event.preventDefault()
            theClient.find( event.target.value )
        }
    }

    if( typeof client == 'string' ){
        input.addEventListener('input', check)  
        input.addEventListener('keyup', process)
    } 
}
