Konekti.plugin.sidebar.open = function( barId ){
 var c = Konekti.util.vc(barId)
 c.style.display = 'block'
}

Konekti.plugin.sidebar.close = function( barId ){ Konekti.util.vc(barId).style.display = 'none' }

Konekti.plugin.sidebar.connect = function( dictionary ){
 var id = dictionary.id
 var client=dictionary.client
 dictionary.content.id = id+'Content'
 Konekti.server.client[id] = { select: function(itemId){ Konekti.server.client[client].select(itemId) } }
 dictionary.content.client = id
 Konekti.plugin[dictionary.content.plugin].replaceWith(dictionary.content)
}
