Konekti.plugin.title.replace = 'strict'

Konekti.plugin.title.htmlCode = function(dictionary){
    if( typeof dictionary.color == 'undefined' ) dictionary.color = 'blue'
    if( typeof dictionary.font == 'undefined' ) dictionary.font = 'xlarge'
    dictionary.pfont = Konekti.util.previousFont(dictionary.font)
    dictionary.himage = Math.floor(1.5*Konekti.util.fontSize(dictionary.font))
    return Konekti.util.fromTemplate( this.htmlTemplate, dictionary, '·' )
}
