css:
/* If the screen size is 601px wide or more, set the font-size of <div> to 80px */
@media screen and (min-width: 601px){
 .titlemain{ font-size: 28px; }
 .titlesecondary{ font-size: 22px; }
 .titleimage{ width:80px; }
}

/* If the screen size is 601px wide or more, set the font-size of <div> to 80px */
@media screen and (max-width: 600px) and (min-width: 301px){
 .titlemain{ font-size: 24px; }
 .titlesecondary{ font-size: 19px; }
 .titleimage{ width:60px; }
}

/* If the screen size is 600px wide or less, set the font-size of <div> to 30px */
@media screen and (max-width: 300px){
 .titleimage{ width:30px; }
 .titlemain{ font-size: 16px; }
 .titlesecondary{ font-size: 12px; }
}
